SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_prescricao_adep (nr_prescricao_p bigint, ds_justificativa_p text, nm_usuario_p text) AS $body$
BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	update	prescr_medica
	set	ie_adep 			= 'S',
		ds_justif_imp_adep		= ds_justificativa_p,
		dt_imp_adep		= clock_timestamp(),
		nm_usuario_imp_adep	= nm_usuario_p,
		nm_usuario		= nm_usuario_p
	where	nr_prescricao 		= nr_prescricao_p;

	/* Rafael 3/7/8 - HSC */

	update	prescr_mat_hor
	set	ie_adep 		= 'S'
	where	nr_prescricao 	= nr_prescricao_p
	and	coalesce(ie_adep,'N') 	= 'N';

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
