CREATE OR REPLACE PROCEDURE atualizar_prescricao_curativo ( nr_sequencia_p bigint, nr_prescricao_p bigint, nr_atendimento_p bigint, cd_perfil_p bigint, ie_tipo_pessoa_p text, ie_prescricao_p text, ie_liberar_p text, nm_usuario_p text) AS $body$
DECLARE

		 
ds_erro_w	varchar(2000);
nr_prescricao_w bigint;

BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin	 
	 
	CALL gerar_prescricao_curativo(nr_sequencia_p, nm_usuario_p);
			 
	nr_prescricao_w := nr_prescricao_p;
	if (coalesce(nr_prescricao_p::text, '') = '') then 
		select	max(nr_prescricao) 
		into STRICT	nr_prescricao_w 
		from	cur_curativo 
		where	nr_sequencia = nr_sequencia_p;
	end if;
	 
	if (ie_liberar_p = 'S') and (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (ie_tipo_pessoa_p IS NOT NULL AND ie_tipo_pessoa_p::text <> '') and (ie_prescricao_p IS NOT NULL AND ie_prescricao_p::text <> '') and (cd_perfil_p IS NOT NULL AND cd_perfil_p::text <> '') then 
		begin 
		 
		ds_erro_w := liberar_prescricao( 
			nr_prescricao_w, nr_atendimento_p, ie_tipo_pessoa_p, cd_perfil_p, nm_usuario_p, ie_prescricao_p, ds_erro_w);
		end;
	end if;
	end;
end if;
commit;
end;
$body$
