CREATE OR REPLACE PROCEDURE atualizar_prescr_del_cih_atx ( nr_prescricao_p bigint, nr_sequencia_p bigint, nr_prescr_anterior_p bigint) AS $body$
DECLARE

 
nr_presc_retorno_w		prescr_material.nr_prescricao%type;
ie_prescr_retorno_w		varchar(1);
BEGIN
SELECT * FROM obter_se_prescr_sug_cih(nr_prescricao_p, nr_sequencia_p, nr_prescr_anterior_p, nr_presc_retorno_w, ie_prescr_retorno_w) INTO STRICT nr_presc_retorno_w, ie_prescr_retorno_w;
 
if (ie_prescr_retorno_w = 'S') and (nr_presc_retorno_w > 0) then 
	update medicamento_cih_sug 
	set ie_concordo = 'S' 
	where nr_prescricao = nr_presc_retorno_w 
	and ie_concordo = 'X';
end if;	
commit;
 
end;
$body$
LANGUAGE PLPGSQL
