CREATE OR REPLACE PROCEDURE atualizar_prescr_lote_gedipa ( cd_estabelecimento_p bigint, nr_prescricao_p bigint, cd_setor_new_p bigint, cd_setor_old_p bigint, nm_usuario_p text) AS $body$
BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	if (obter_se_setor_processo_gedipa(cd_setor_old_p) = 'N') and (obter_se_setor_processo_gedipa(cd_setor_new_p) = 'S') then
		begin
		update	prescr_mat_hor a
		set	a.ie_gedipa		= 'N'
		where	a.nr_prescricao		= nr_prescricao_p
		and	(a.dt_emissao_farmacia IS NOT NULL AND a.dt_emissao_farmacia::text <> '')
		and	obter_se_material_padronizado(cd_estabelecimento_p,cd_material) = 'S';

		update	prescr_mat_hor a
		set	a.ie_gedipa		= 'N'
		where	a.nr_prescricao		= nr_prescricao_p
		and	(a.dt_emissao_farmacia IS NOT NULL AND a.dt_emissao_farmacia::text <> '')
		and	a.dt_horario		< clock_timestamp()
		and	obter_se_material_padronizado(cd_estabelecimento_p,cd_material) = 'N';

		update	ap_lote
		set	dt_impressao	= clock_timestamp()
		where	nr_prescricao	= nr_prescricao_p
		and	coalesce(dt_impressao::text, '') = '';
		end;
	/*elsif	(obter_se_setor_processo_gedipa(cd_setor_old_p) = 'S') and
		(obter_se_setor_processo_gedipa(cd_setor_new_p) = 'N') then
		begin
		update	prescr_mat_hor


		end;*/
	end if;
	end;
end if;

commit;

end;
$body$
