CREATE OR REPLACE PROCEDURE atualizar_prescr_material ( nr_sequencia_solucao_p bigint, nr_prescricao_p bigint, qt_dose_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w	integer;

BEGIN
if (nr_sequencia_solucao_p IS NOT NULL AND nr_sequencia_solucao_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	begin
	select	max(a.nr_sequencia)
	into STRICT	nr_sequencia_w
	from	material b,
		prescr_material a
	where	a.nr_prescricao		= nr_prescricao_p
	and	a.nr_sequencia_solucao	= nr_sequencia_solucao_p
	and	a.cd_material		= b.cd_material
	and	coalesce(b.ie_ancora_solucao,'N') = 'S';

	update	prescr_material
	set	qt_dose			= qt_dose_p,
		qt_solucao		= qt_dose_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_prescricao		= nr_prescricao_p
	and	nr_sequencia_solucao	= nr_sequencia_solucao_p
	and	nr_sequencia		= nr_sequencia_w;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
