CREATE OR REPLACE PROCEDURE atualizar_prim_horario_prescr ( nr_prescricao_p bigint, nr_atendimento_p bigint, dt_prescricao_p timestamp, cd_setor_atendimento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, nr_horas_validade_p INOUT bigint, prim_horario_prescr_p INOUT timestamp ) AS $body$
DECLARE

 
 
prim_horario_prescr_w	timestamp;	
ie_estender_validade_w	varchar(10);
ie_calcular_validade_w	varchar(1);
nr_horas_validade_w	double precision;
cd_setor_atendimento_w	integer;
	

BEGIN 
 
prim_horario_prescr_w 	:= obter_prim_horario_prescricao(nr_atendimento_p, nr_atendimento_p, dt_prescricao_p, nm_usuario_p,'R');
ie_calcular_validade_w	:= obter_valor_param_usuario(924, 98, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p);
 
if (ie_calcular_validade_w	= 'R') then 
	select	max(cd_setor_atendimento) 
	into STRICT	cd_setor_atendimento_w 
	from	prescr_medica 
	where	nr_prescricao	= nr_prescricao_p;
	 
	ie_calcular_validade_w	:= obter_se_calcula_validade(cd_setor_atendimento_w);
	 
end if;
 
if (ie_calcular_validade_w <> 'N') then 
	begin 
	ie_estender_validade_w 	:= obter_valor_param_usuario(924, 249, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p);
	nr_horas_validade_w	:= obter_horas_validade_prescr(prim_horario_prescr_w, nr_atendimento_p, ie_estender_validade_w, 'A', dt_prescricao_p, nr_prescricao_p);
	end;
end if;
 
prim_horario_prescr_p 	:= prim_horario_prescr_w;
nr_horas_validade_p	:= nr_horas_validade_w;
 
commit;
 
end;
$body$
