CREATE OR REPLACE PROCEDURE atualizar_probab_alta_atend ( nr_atendimento_p bigint, ie_probabilidade_alta_p text, dt_previsto_alta_p timestamp, dt_prescricao_p timestamp, nm_usuario_p text) AS $body$
BEGIN
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (ie_probabilidade_alta_p IS NOT NULL AND ie_probabilidade_alta_p::text <> '') and (dt_previsto_alta_p IS NOT NULL AND dt_previsto_alta_p::text <> '') and (dt_prescricao_p IS NOT NULL AND dt_prescricao_p::text <> '') then 
	if (dt_previsto_alta_p < dt_prescricao_p) then 
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(202692);
	end if;
	 
	update	atendimento_paciente 
	set	ie_probabilidade_alta = ie_probabilidade_alta_p, 
		dt_previsto_alta = dt_previsto_alta_p, 
		nm_usuario = nm_usuario_p 
	where	nr_atendimento = nr_atendimento_p;
	 
	commit;
end if;
 
commit;
 
end;
