CREATE OR REPLACE PROCEDURE atualizar_proced_mat_exame (nr_seq_exame_p bigint, cd_convenio_p bigint, cd_categoria_p text, ie_tipo_atendimento_p bigint, cd_estabelecimento_p bigint, ie_tipo_convenio_p bigint, nr_seq_proc_interno_p bigint, cd_material_exame_p text, nr_prescricao_p bigint, nr_seq_proced_p bigint) AS $body$
DECLARE

 
ds_erro_w			varchar(2000);
nr_seq_proc_interno_aux_w	bigint;
cd_setor_exclusivo_w		bigint;
cd_procedimento_w		bigint;
cd_proced_prescr_w		bigint;
ie_origem_proced_w		bigint;
cd_plano_convenio_w		varchar(10);
			

BEGIN 
 
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then 
	select	max(substr(Obter_Plano_Conv_Atend(a.nr_atendimento),1,10)) 
	into STRICT	cd_plano_convenio_w 
	from	prescr_medica a 
	where	a.nr_prescricao = nr_prescricao_p;
end if;
 
SELECT * FROM Obter_Exame_Lab_Convenio(nr_seq_exame_p, cd_convenio_p, cd_categoria_p, ie_tipo_atendimento_p, cd_estabelecimento_p, ie_tipo_convenio_p, nr_seq_proc_interno_p, cd_material_exame_p, cd_plano_convenio_w, cd_setor_exclusivo_w, cd_procedimento_w, ie_origem_proced_w, ds_erro_w, nr_seq_proc_interno_aux_w) INTO STRICT cd_setor_exclusivo_w, cd_procedimento_w, ie_origem_proced_w, ds_erro_w, nr_seq_proc_interno_aux_w;
			 
select	max(cd_procedimento) 
into STRICT	cd_proced_prescr_w 
from	prescr_procedimento 
where	nr_prescricao		= nr_prescricao_p 
and	nr_sequencia		= nr_seq_proced_p;
			 
if (cd_procedimento_w > 0) and (cd_procedimento_w <> cd_proced_prescr_w) then 
	update	prescr_procedimento 
	set	cd_setor_atendimento	= cd_setor_exclusivo_w, 
		cd_procedimento		= cd_procedimento_w, 
		ie_origem_proced	= ie_origem_proced_w 
	where	nr_prescricao		= nr_prescricao_p 
	and	nr_sequencia		= nr_seq_proced_p;
	 
	commit;
	 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
