CREATE OR REPLACE PROCEDURE atualizar_proc_material_atend ( cd_setor_atendimento_p bigint, nr_seq_procpaci_p bigint, nr_seq_material_atend_pac_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_altera_data_conta_w	varchar(10);
dt_exec_proced_w	timestamp;


BEGIN

ie_altera_data_conta_w  := Obter_Param_Usuario(942, 358, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_altera_data_conta_w );

if (ie_altera_data_conta_w = 'S') then

	select	dt_procedimento
	into STRICT	dt_exec_proced_w
	from	procedimento_paciente
	where	nr_sequencia = nr_seq_procpaci_p;

	update  material_atend_paciente
	set	nr_seq_proc_princ = nr_seq_procpaci_p,
		dt_atendimento = coalesce(dt_exec_proced_w,dt_atendimento),
		dt_conta = coalesce(dt_exec_proced_w,dt_conta),
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_seq_material_atend_pac_p;

else
	update  material_atend_paciente
	set	nr_seq_proc_princ = nr_seq_procpaci_p,
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_seq_material_atend_pac_p;


end if;


commit;

end;
$body$
