CREATE OR REPLACE PROCEDURE atualizar_proc_princ_mat_glosa ( nr_seq_mat_audit_p bigint, nr_conta_destino_p bigint, nr_conta_origem_p bigint, nr_seq_audit_origem_p bigint, nr_seq_motivo_p bigint, ie_altera_item_repasse_p text, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_material_w	material_atend_paciente.nr_sequencia%type;
ie_repasse_gerado_w	varchar(1);
vl_material_w		auditoria_matpaci.vl_material%type;


BEGIN 
 
if (coalesce(nr_seq_mat_audit_p,0) > 0) then 
 
	select	max(nr_seq_matpaci), 
		max(vl_material) 
	into STRICT	nr_seq_material_w, 
		vl_material_w 
	from	auditoria_matpaci 
	where	nr_sequencia = nr_seq_mat_audit_p;
	 
 
	ie_repasse_gerado_w	:= substr(obter_se_proc_mat_repasse(nr_seq_material_w,'M'),1,1);
	 
	if (coalesce(ie_repasse_gerado_w,'N') = 'N') or (coalesce(ie_altera_item_repasse_p,'S') = 'S') then 
 
		update	material_atend_paciente 
		set	nr_seq_proc_princ  = NULL, 
			dt_atualizacao = clock_timestamp(), 
			nm_usuario = nm_usuario_p 
		where	nr_sequencia = nr_seq_material_w;
		 
		CALL trocar_conta_auditoria(nr_conta_destino_p, nr_seq_material_w, 2, nm_usuario_p, nr_seq_motivo_p, 'N', 'N', 'N');
 
		CALL transferir_conta_auditoria(nr_seq_material_w, 2, nm_usuario_p);
 
		CALL gravar_audit_hist('M', null, nr_seq_mat_audit_p, vl_material_w, nr_conta_origem_p, nr_seq_audit_origem_p, nm_usuario_p);
		 
	end if;
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
