CREATE OR REPLACE PROCEDURE atualizar_prof_escala_diaria ( nr_sequencia_p bigint, cd_pessoa_p bigint, ie_origem_p text, nm_usuario_p text) AS $body$
BEGIN
if (coalesce(cd_pessoa_p,'0') <> '0') then
	begin
	if (ie_origem_p = 'S') then
		update	escala_diaria
		set	cd_pessoa_origem = cd_pessoa_p,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where	nr_sequencia = nr_sequencia_p;
	else
		update	escala_diaria
		set	cd_pessoa_fisica = cd_pessoa_p,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where	nr_sequencia = nr_sequencia_p;
	end if;

	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
