CREATE OR REPLACE PROCEDURE atualizar_proj_rat_mes ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_total_rat_w			double precision;
QT_TOTAL_HORA_w			double precision;

BEGIN

select	coalesce(sum(a.vl_pagar),0),
	coalesce(sum(coalesce(QT_TOTAL_HORA,0)),0)
into STRICT	vl_total_rat_w,
	QT_TOTAL_HORA_w
from	proj_rat a
where	nr_seq_fech_mes = nr_sequencia_p;

update	proj_rat_mes
set	vl_total_pagar	= vl_total_rat_w,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p,
	qt_hora		= QT_TOTAL_HORA_w
where	nr_sequencia	= nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
