CREATE OR REPLACE PROCEDURE atualizar_propaci_med_arquivo ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, cd_medico_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_propaci_w	bigint;
cd_medico_exec_w	varchar(10);
ie_medico_w	varchar(1);
nr_seq_interno_w	bigint;


BEGIN

select	max(b.nr_sequencia)
into STRICT	nr_seq_propaci_w
from	procedimento_paciente b,
		prescr_procedimento a
where	a.nr_prescricao		= b.nr_prescricao
and		a.nr_sequencia		= b.nr_sequencia_prescricao
and		a.nr_prescricao		= nr_prescricao_p
and		a.nr_sequencia		= nr_seq_prescr_p;

if (nr_seq_propaci_w IS NOT NULL AND nr_seq_propaci_w::text <> '') then

	ie_medico_w	:= Obter_se_medico(cd_medico_p,'M');

	if (ie_medico_w = 'S')	then

		update	procedimento_paciente
		set		cd_medico_executor = cd_medico_p,
				dt_atualizacao = clock_timestamp(),
				nm_usuario = nm_usuario_p
		where	nr_sequencia = nr_seq_propaci_w;

	end if;

end if;

commit;

end;
