CREATE OR REPLACE PROCEDURE atualizar_qt_dependente_pf (cd_pessoa_fisica_p text, qt_pendente_p bigint) AS $body$
DECLARE



qt_pendentes_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_pendentes_w
from	pessoa_fisica_dependente
where	cd_pessoa_fisica = cd_pessoa_fisica_p;

if (qt_pendentes_w <> qt_pendente_p) then
	Update	pessoa_fisica
	set	qt_dependente = qt_pendentes_w
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
