CREATE OR REPLACE PROCEDURE atualizar_raas_lote_atend ( nr_seq_lote_atend_p bigint, is_home_care text, nr_sequencia_p bigint, ie_remover_p text default 'N') AS $body$
DECLARE


ie_status_lote_w		raas_lote_atendimentos.ie_status_lote%type;


BEGIN
if (ie_remover_p = 'N') then
	if (coalesce(nr_seq_lote_atend_p,0) > 0) and (coalesce(nr_sequencia_p,0) > 0) then
		if (is_home_care = 'S') then
			update	raas_atend_atencao_dom
			set		nr_seq_lote_atend = nr_seq_lote_atend_p,
					dt_atualizacao = clock_timestamp(),
					nm_usuario = obter_usuario_ativo
			where	nr_sequencia = nr_sequencia_p;
		else
			update	raas_atend_atencao_psico
			set		nr_seq_lote_atend = nr_seq_lote_atend_p,
					dt_atualizacao = clock_timestamp(),
					nm_usuario = obter_usuario_ativo
			where	nr_sequencia = nr_sequencia_p;
		end if;
		commit;
	end if;
else
	select	max(ie_status_lote)
	into STRICT	ie_status_lote_w
	from (SELECT	ie_status_lote
			from	raas_lote_atendimentos a,
					raas_atend_atencao_dom b
			where	a.nr_sequencia = b.nr_seq_lote_atend
			and		b.nr_sequencia = nr_sequencia_p
			
union

			SELECT	ie_status_lote
			from	raas_lote_atendimentos a,
					raas_atend_atencao_psico b
			where	a.nr_sequencia = b.nr_seq_lote_atend
			and		b.nr_sequencia = nr_sequencia_p) alias1;
			
	if (ie_status_lote_w = '1') then
		if (is_home_care = 'S') then
			update	raas_atend_atencao_dom
			set		nr_seq_lote_atend  = NULL,
					dt_atualizacao = clock_timestamp(),
					nm_usuario = obter_usuario_ativo
			where	nr_sequencia = nr_sequencia_p;
		else
			update	raas_atend_atencao_psico
			set		nr_seq_lote_atend  = NULL,
					dt_atualizacao = clock_timestamp(),
					nm_usuario = obter_usuario_ativo
			where	nr_sequencia = nr_sequencia_p;
		end if;
		commit;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
