CREATE OR REPLACE PROCEDURE atualizar_raas_status_lote ( nr_sequencia_p bigint) AS $body$
DECLARE


ie_status_lote_w		raas_lote_atendimentos.ie_status_lote%type;


BEGIN

select	max(ie_status_lote)
into STRICT	ie_status_lote_w
from	raas_lote_atendimentos
where	nr_sequencia = nr_sequencia_p;

if (ie_status_lote_w IS NOT NULL AND ie_status_lote_w::text <> '') then
	update	raas_lote_atendimentos
	set		ie_status_lote = CASE WHEN ie_status_lote_w='1' THEN  '2'  ELSE '1' END ,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = obter_usuario_ativo
	where	nr_sequencia = nr_sequencia_p;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
