CREATE OR REPLACE PROCEDURE atualizar_realizacao_ativ_proj ( nr_seq_atividade_p bigint, pr_realizacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_cronograma_w	bigint;
nr_seq_projeto_w	bigint;


BEGIN
if (nr_seq_atividade_p IS NOT NULL AND nr_seq_atividade_p::text <> '') and (pr_realizacao_p IS NOT NULL AND pr_realizacao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	CALL proj_atualizar_realiz_etapa(nr_seq_atividade_p, pr_realizacao_p, nm_usuario_p);

	select	coalesce(max(nr_seq_cronograma),0),
		max(c.NR_SEQ_PROJ)
	into STRICT	nr_seq_cronograma_w,
		nr_seq_projeto_w
	from	proj_cronograma c,
		proj_cron_etapa e
	where	c.nr_sequencia = e.nr_seq_cronograma
	and	e.nr_sequencia = nr_seq_atividade_p;

	if (nr_seq_cronograma_w > 0) then
		begin
		CALL proj_desenv_atual_perc_cron(nr_seq_cronograma_w, nm_usuario_p);
		end;
	end if;

	if (nr_seq_projeto_w IS NOT NULL AND nr_seq_projeto_w::text <> '') then

		select	coalesce(max(nr_seq_cronograma),0)
		into STRICT	nr_seq_cronograma_w
		from	proj_cronograma c,
			proj_cron_etapa e
		where	c.nr_sequencia = e.nr_seq_cronograma
		and	e.NR_SEQ_SUB_PROJ = nr_seq_projeto_w;

		if (nr_seq_cronograma_w > 0) then

			CALL proj_desenv_atual_perc_cron(nr_seq_cronograma_w, nm_usuario_p);

		end if;

	end if;

	end;
end if;



commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
