CREATE OR REPLACE PROCEDURE atualizar_reconstituicao ( nr_prescricao_p bigint, nr_sequencia_p bigint, cd_material_p bigint, cd_perfil_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text ) AS $body$
DECLARE

					
ie_reg_alteracao_farm_w	varchar(1);


BEGIN
ie_reg_alteracao_farm_w := obter_param_usuario(7010, 48, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, ie_reg_alteracao_farm_w);

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
	
	update   prescr_material
	set      ds_diluicao_edit  = NULL
	where    nr_prescricao = nr_prescricao_p
	and      nr_sequencia = nr_sequencia_p;
	
	if (ie_reg_alteracao_farm_w = 'S') then
		CALL gerar_historico_dil_red_rec( 'N',
									 3, 
									 9, 
									 nr_prescricao_p,
									 nr_sequencia_p,
									 cd_material_p,
									 null, 
									 null, 
									 null, 
									 null, 
									 null, 
									 null, 
									 null,
									 cd_estabelecimento_p, 
									 nm_usuario_p );
	end if;
	
	end;
end if;

commit;

end;
