SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_red_medic_soluc_onc ( nr_seq_paciente_p bigint, nr_seq_material_p bigint, pr_reducao_p bigint, ie_opcao_p text, ie_acao_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


/*
			ie_opcao_p
Alterar Medicamento     	'M'
Alterar Solução		'S'

			ie_acao_p
Consultar		'C'
Executar		'E'
*/
ds_retorno_w	varchar(255) := '';
qt_reg_w		bigint;


BEGIN

if (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then

	If ( ie_acao_p = 'C' ) then

		if ( ie_opcao_p = 'M' ) then

			Select 	count(*)
			into STRICT	qt_reg_w
			from   	paciente_protocolo_medic
			where  	nr_seq_paciente = nr_seq_paciente_p
			and    	cd_material = nr_seq_material_p
			and    	coalesce(ie_aplica_reducao,'S') = 'S'
			and    	coalesce(nr_seq_diluicao::text, '') = ''
			and    	coalesce(nr_seq_solucao::text, '') = ''
			and    	coalesce(nr_seq_medic_material::text, '') = ''
			and    	coalesce(nr_seq_procedimento::text, '') = ''
			and		pr_reducao <> pr_reducao_p;

			if (qt_reg_w > 0) then
				ds_retorno_w := obter_desc_expressao(782555)/*'Existe o mesmo medicamento na pasta medicamentos do protocolo com percentual de redução diferente.'*/
 || obter_desc_expressao(782563); --'Deseja realizar a alteração do percentual nessa pasta?';
			end if;

		else

			Select 	count(*)
			into STRICT	qt_reg_w
			from   	paciente_protocolo_medic
			where  	nr_seq_paciente = nr_seq_paciente_p
			and    	cd_material = nr_seq_material_p
			and    	coalesce(ie_aplica_reducao,'S') = 'S'
			and		coalesce(ie_zerado,'N') = 'N'
			and    	(nr_seq_solucao IS NOT NULL AND nr_seq_solucao::text <> '')
			and		pr_reducao <> pr_reducao_p;

			if (qt_reg_w > 0) then
				ds_retorno_w := obter_desc_expressao(782555)/*'Existe o mesmo medicamento na pasta soluções do protocolo com percentual de redução diferente.'*/
 ||obter_desc_expressao(782557); --'Deseja realizar a alteração do percentual na solução?';
			end if;

		end if;


	end if;

	If ( ie_acao_p = 'E' ) and (pr_reducao_p IS NOT NULL AND pr_reducao_p::text <> '') and ( pr_reducao_p > 0)	then

		if ( ie_opcao_p = 'M' ) then

			update	paciente_protocolo_medic
			set		qt_dose_prescr	= (qt_dose_prescr - ((pr_reducao_p * coalesce(qt_dose_prescr,0)) / 100)),
					pr_reducao = pr_reducao_p
			where  	nr_seq_paciente = nr_seq_paciente_p
			and    	cd_material = nr_seq_material_p
			and    	coalesce(ie_aplica_reducao,'S') = 'S'
			and    	coalesce(nr_seq_diluicao::text, '') = ''
			and    	coalesce(nr_seq_solucao::text, '') = ''
			and    	coalesce(nr_seq_medic_material::text, '') = ''
			and    	coalesce(nr_seq_procedimento::text, '') = '';

		else

			update	paciente_protocolo_medic
			set		qt_dose_prescr	= (qt_dose_prescr - ((pr_reducao_p * coalesce(qt_dose_prescr,0)) / 100)),
					pr_reducao = pr_reducao_p
			where  	nr_seq_paciente = nr_seq_paciente_p
			and    	cd_material = nr_seq_material_p
			and    	coalesce(ie_aplica_reducao,'S') = 'S'
			and		coalesce(ie_zerado,'N') = 'N'
			and    	(nr_seq_solucao IS NOT NULL AND nr_seq_solucao::text <> '');

		end if;

		commit;

	end if;
end if;

ds_retorno_p := ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualizar_red_medic_soluc_onc ( nr_seq_paciente_p bigint, nr_seq_material_p bigint, pr_reducao_p bigint, ie_opcao_p text, ie_acao_p text, ds_retorno_p INOUT text) FROM PUBLIC;

