CREATE OR REPLACE PROCEDURE atualizar_ref_protocolo_conv (nr_seq_protocolo_p bigint, dt_mesano_referencia_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint, ds_justificativa_p text) AS $body$
DECLARE

 
		 
ie_grava_log_w			varchar(01);
vl_protocolo_w			double precision;
dt_mesano_referencia_w		timestamp;
ie_atualiza_entrega_w		varchar(01);
ie_atualiza_ref_filtro_w	varchar(1);
ds_texto_w			protocolo_convenio_log.ds_observacao%type;


BEGIN 
 
ie_grava_log_w := Obter_Param_Usuario(85, 141, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_grava_log_w);
ie_atualiza_entrega_w := Obter_Param_Usuario(85, 210, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_atualiza_entrega_w);
ie_atualiza_ref_filtro_w := Obter_Param_Usuario(85, 218, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_atualiza_ref_filtro_w);
 
select dt_mesano_referencia 
into STRICT	dt_mesano_referencia_w 
from	protocolo_convenio 
where	nr_seq_protocolo = nr_seq_protocolo_p;
 
if (ie_grava_log_w = 'S') then 
	if (dt_mesano_referencia_w <> dt_mesano_referencia_p) then 
	 
		select 	coalesce(obter_total_protocolo(nr_seq_protocolo_p),0)		 
		into STRICT	vl_protocolo_w 
		;
		 
		--Data de ReferÃªncia(De: #@DT_MESANO_REFERENCIA#@ p/ #@DT_MESANO_REFERENCIA_P#@ ) 
		ds_texto_w := substr(wheb_mensagem_pck.get_texto(301258,'DT_MESANO_REFERENCIA='|| dt_mesano_referencia_w || 
								';DT_MESANO_REFERENCIA_P='|| dt_mesano_referencia_p ),1,255);
		 
		insert into protocolo_convenio_log(nr_sequencia, 
			dt_atualizacao, 
			nm_usuario, 
			nr_seq_protocolo, 
			ds_arquivo_envio, 
			vl_protocolo, 
			ie_tipo_log, 
			ds_observacao, 
			ds_justificativa) 
		SELECT 	nextval('protocolo_convenio_log_seq'), 
			clock_timestamp(), 
			nm_usuario_p, 
			a.nr_seq_protocolo, 
			a.ds_arquivo_envio, 
			vl_protocolo_w, 
			'D', 
			ds_texto_w, 
			substr(ds_justificativa_p,1,255) 
		from	protocolo_convenio a 
		where	a.nr_seq_protocolo = nr_seq_protocolo_p;
	end if;
end if;
 
update	protocolo_convenio 
set	dt_mesano_referencia	= dt_mesano_referencia_p, 
	nm_usuario		= nm_usuario_p, 
	dt_atualizacao		= clock_timestamp() 
where	nr_seq_protocolo	= nr_seq_protocolo_p;
 
update	conta_paciente 
set	dt_mesano_referencia	= dt_mesano_referencia_p, 
	nm_usuario		= nm_usuario_p, 
	dt_atualizacao		= clock_timestamp() 
where	nr_seq_protocolo	= nr_seq_protocolo_p;
 
if (ie_atualiza_entrega_w = 'S') then 
	update	protocolo_convenio 
	set	dt_entrega_convenio = dt_mesano_referencia_p 
	where	nr_seq_protocolo = nr_seq_protocolo_p;
end if;
 
if (ie_atualiza_ref_filtro_w = 'S') then 
	update	protocolo_convenio 
	set	dt_mesano_ref_par = dt_mesano_referencia_p 
	where	nr_seq_protocolo = nr_seq_protocolo_p;
end if;
 
commit;
 
end;
$body$
