SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_registro_cobranca ( nr_sequencia_p bigint, ie_acao_p text) AS $body$
DECLARE


/*IE_ACAO_P
I = Incluir como Indevido.
D = Desfazer como Indevido.
*/
BEGIN

if ( coalesce(nr_sequencia_p,0) <> 0 ) then

	if (ie_acao_p = 'I') then

		update	registro_cobranca
		set		ie_cobranca_indevida 	= 'S'
		where	nr_sequencia			= nr_sequencia_p;

	elsif (ie_acao_p = 'D') then

		update	registro_cobranca
		set		ie_cobranca_indevida 	= 'N'
		where	nr_sequencia			= nr_sequencia_p;

	end if;

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
