SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_regra_copia_rep (nr_sequencia_p bigint) AS $body$
DECLARE


ie_regra_geral_w	varchar(2);


BEGIN

select	max(ie_regra_geral)
into STRICT	ie_regra_geral_w
from	rep_regra_copia_crit
where	nr_sequencia = nr_sequencia_p;

if (ie_regra_geral_w	= 'H') then
	begin
		update	rep_regra_copia_hor
		set	ie_regra_prim_hor	 = NULL
		where	nr_seq_regra_copia	= nr_sequencia_p;
	end;
elsif (ie_regra_geral_w = '1H') then
	begin
		update	rep_regra_copia_hor
		set	ie_regra_horarios  = NULL,
			ie_regra_prim_horarios  = NULL,
			ie_elimina_hor = 'S',
			ie_reordenar = 'S'
		where	nr_seq_regra_copia = nr_sequencia_p;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
