CREATE OR REPLACE PROCEDURE atualizar_regra_preco_consig ( nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w			bigint;
ie_valor_consignado_w		varchar(15);

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	coalesce(a.ie_valor_consignado,'N')
from	regra_valor_ordem_consig a
where not exists (
	SELECT	1
	from	regra_preco_ordem_consig x
	where	x.nr_seq_regra = a.nr_sequencia);


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	ie_valor_consignado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	insert into regra_preco_ordem_consig(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_regra,
		ie_valor_consignado,
		nr_seq_prioridade)
	values (	nextval('regra_preco_ordem_consig_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_sequencia_w,
		ie_valor_consignado_w,
		1);

	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
