CREATE OR REPLACE PROCEDURE atualizar_regra_tipo_pag_banc (nr_seq_escrit_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Pagamento Escritural - Atualizar as regras de tipo de pagamento do lote bancário
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/nr_titulo_w		bigint;
ie_tipo_pessoa_w	varchar(2);

C01 CURSOR FOR
	SELECT  nr_titulo
	from    titulo_pagar_escrit
	where   coalesce(nr_seq_regra_tipo_pag::text, '') = ''
	and     nr_seq_escrit = nr_seq_escrit_p;


BEGIN
open C01;
loop
fetch C01 into
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	CASE WHEN coalesce(cd_pessoa_fisica::text, '') = '' THEN 'PJ'  ELSE 'PF' END
	into STRICT	ie_tipo_pessoa_w
	from	titulo_pagar
	where	nr_titulo = nr_titulo_w;

	CALL atualiza_regra_tipo_pag_escrit(nr_seq_escrit_p,nr_titulo_w,ie_tipo_pessoa_w);
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
