CREATE OR REPLACE PROCEDURE atualizar_repasse_cheque (cd_estabelecimento_p bigint, qt_dias_uteis_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_interno_conta_w		bigint;
vl_saldo_titulo_w		double precision;
dt_deposito_w			timestamp;
cd_estabelecimento_w		smallint;

c01 CURSOR FOR
SELECT	b.nr_interno_conta,
	c.vl_saldo_titulo,
	trunc(coalesce(e.dt_reapresentacao, e.dt_deposito),'dd'),
	c.cd_estabelecimento
from	convenio d,
	procedimento_paciente b,
	procedimento_repasse a,
	titulo_receber c,
	cheque_cr e
where	coalesce(a.nr_repasse_terceiro::text, '') = ''
and	a.nr_seq_procedimento	= b.nr_sequencia
and	coalesce(b.cd_motivo_exc_conta::text, '') = ''
and	b.nr_interno_conta	= c.nr_interno_conta
and	b.cd_convenio		= d.cd_convenio
and	d.ie_tipo_convenio	= 1
and	c.cd_estabelecimento	= coalesce(cd_estabelecimento_p,c.cd_estabelecimento)
and	c.nr_titulo		= e.nr_titulo
and	a.ie_status		in ('A','U','D')		/* Jacson OS 30239 - inclui 'D'*/
and	(coalesce(e.dt_reapresentacao, e.dt_deposito) IS NOT NULL AND (coalesce(e.dt_reapresentacao, e.dt_deposito))::text <> '')
and	coalesce(e.dt_devolucao::text, '') = ''
and	coalesce(e.dt_seg_devolucao::text, '') = ''

union

select	b.nr_interno_conta,
	c.vl_saldo_titulo,
	trunc(coalesce(e.dt_reapresentacao, e.dt_deposito),'dd'),
	c.cd_estabelecimento
from    convenio d,
	material_atend_paciente b,
	material_repasse a,
	titulo_receber c,
	cheque_cr e
where	coalesce(a.nr_repasse_terceiro::text, '') = ''
and	a.nr_seq_material	= b.nr_sequencia
and	coalesce(b.cd_motivo_exc_conta::text, '') = ''
and	b.nr_interno_conta	= c.nr_interno_conta
and	b.cd_convenio		= d.cd_convenio
and	d.ie_tipo_convenio	= 1
and	c.cd_estabelecimento	= coalesce(cd_estabelecimento_p,c.cd_estabelecimento)
and	c.nr_titulo		= e.nr_titulo
and	a.ie_status		in ('A','U','D')		/* Jacson OS 30239 - inclui 'D'*/
and	(coalesce(e.dt_reapresentacao, e.dt_deposito) IS NOT NULL AND (coalesce(e.dt_reapresentacao, e.dt_deposito))::text <> '')
and	coalesce(e.dt_devolucao::text, '') = ''
and	coalesce(e.dt_seg_devolucao::text, '') = '';


BEGIN


open c01;
loop
fetch c01 into
	nr_interno_conta_w,
	vl_saldo_titulo_w,
	dt_deposito_w,
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	if (OBTER_DIAS_UTEIS_PERIODO(dt_deposito_w, clock_timestamp(), cd_estabelecimento_w) >= qt_dias_uteis_p) then
		CALL Atualizar_Repasse_Conta(nr_interno_conta_w, vl_saldo_titulo_w, nm_usuario_p);
	end if;

end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualizar_repasse_cheque (cd_estabelecimento_p bigint, qt_dias_uteis_p bigint, nm_usuario_p text) FROM PUBLIC;

