SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_repasse_conta ( NR_INTERNO_CONTA_P bigint, VL_SALDO_P bigint, NM_USUARIO_P text) AS $body$
DECLARE


nr_seq_repasse_w		bigint;
vl_repasse_w		double precision;
nr_seq_proc_w		bigint;
nr_seq_mat_w		bigint;

ie_status_retorno_w	varchar(1);

C01 CURSOR FOR
	SELECT	b.nr_seq_procedimento,
		b.nr_sequencia,
		b.vl_repasse
	from	procedimento_repasse b,
		procedimento_paciente a
	where	a.nr_sequencia		= b.nr_seq_procedimento
	  and	a.nr_interno_conta	= nr_interno_conta_p
	  and	b.ie_status		in ('A','D');/* Jacson OS 30239 - inclui 'D'*/
C02 CURSOR FOR
	SELECT	b.nr_seq_material,
		b.nr_sequencia,
		b.vl_repasse
	from	material_repasse b,
		material_atend_paciente a
	where	a.nr_sequencia		= b.nr_seq_material
	  and	a.nr_interno_conta	= nr_interno_conta_p
	  and	b.ie_status		in ('A','D');/* Jacson OS 30239 - inclui 'D'*/
BEGIN

ie_status_retorno_w := 'A';
if (vl_saldo_p = 0) then
	ie_status_retorno_w := 'R';
end if;

if (ie_status_retorno_w = 'R') then
	open C01;
	loop
	fetch C01 into	nr_seq_proc_w,
			nr_seq_repasse_w,
			vl_repasse_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */

		update procedimento_repasse
		set	vl_liberado = vl_repasse,
			ie_status = ie_status_retorno_w,
			dt_liberacao = clock_timestamp(),
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where nr_sequencia = nr_seq_repasse_w;
	end loop;
	close C01;

	open C02;
	loop
	fetch C02 into	nr_seq_mat_w,
			nr_seq_repasse_w,
			vl_repasse_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */

		update material_repasse
		set	vl_liberado	= vl_repasse,
			ie_status	= ie_status_retorno_w,
			dt_liberacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where nr_sequencia	= nr_seq_repasse_w;
	end loop;
	close C02;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
