SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_repasse_retorno_fixo ( nr_seq_retorno_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_retorno_w			timestamp;
nr_seq_repasse_w		bigint;
ie_glosar_valor_fixo_w	varchar(10);
cd_estabelecimento_w	bigint;

c01 CURSOR FOR
SELECT	b.nr_sequencia
from	convenio_retorno_glosa c,
		material_repasse b,
		material_atend_paciente a,
		convenio_retorno_item e
where	a.nr_sequencia		= b.nr_seq_material
and		a.nr_sequencia		= c.nr_seq_matpaci
and		c.nr_seq_ret_item		= e.nr_sequencia
and		((obter_se_criterio_valor_fixo(null, a.nr_seq_mat_crit_repasse) = 'S') and (ie_glosar_valor_fixo_w = 'S'))
and		b.ie_status			= 'A'
and		e.nr_seq_retorno		= nr_seq_retorno_p;

c02 CURSOR FOR
SELECT	b.nr_sequencia
from	convenio_retorno_glosa c,
		procedimento_repasse b,
		procedimento_paciente a,
		convenio_retorno_item e
where	a.nr_sequencia		= b.nr_seq_procedimento
and		a.nr_sequencia		= c.nr_seq_propaci
and		c.nr_seq_ret_item		= e.nr_sequencia
and		((obter_se_criterio_valor_fixo(a.nr_seq_proc_crit_repasse, null) = 'S') and (ie_glosar_valor_fixo_w = 'S'))
and		b.ie_status			= 'A'
and		e.nr_seq_retorno		= nr_seq_retorno_p;


BEGIN

select	CASE WHEN b.ie_data_lib_repasse_ret='F' THEN coalesce(a.dt_baixa_cr, a.dt_fechamento) WHEN b.ie_data_lib_repasse_ret='R' THEN a.dt_retorno WHEN b.ie_data_lib_repasse_ret='E' THEN  a.dt_fechamento END ,
		b.cd_estabelecimento
into STRICT		dt_retorno_w,
		cd_estabelecimento_w
from	parametro_faturamento b,
		convenio_retorno a
where	a.nr_sequencia		= nr_seq_retorno_p
and		a.cd_estabelecimento	= b.cd_estabelecimento;

ie_glosar_valor_fixo_w := obter_param_usuario(27, 130, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_glosar_valor_fixo_w);

open c01;
loop
fetch C01 into
	nr_seq_repasse_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

		update	material_repasse
		set		vl_repasse			= vl_repasse,
				vl_liberado			= vl_repasse,
				ie_status			= 'R',
				dt_liberacao			= dt_retorno_w,
				nm_usuario			= nm_usuario_p,
				dt_atualizacao		= clock_timestamp(),
				nm_usuario_lib		= nm_usuario_p
		where	nr_sequencia			= nr_seq_repasse_w;

end loop;
close c01;

open c02;
loop
fetch C02 into
	nr_seq_repasse_w;
EXIT WHEN NOT FOUND; /* apply on C02 */

		update	procedimento_repasse
		set		vl_repasse			= vl_repasse,
				vl_liberado			= vl_repasse,
				ie_status			= 'R',
				dt_liberacao			= dt_retorno_w,
				nm_usuario			= nm_usuario_p,
				dt_atualizacao		= clock_timestamp(),
				nm_usuario_lib		= nm_usuario_p
		where	nr_sequencia			= nr_seq_repasse_w;

end loop;
close c02;

end			;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualizar_repasse_retorno_fixo ( nr_seq_retorno_p bigint, nm_usuario_p text) FROM PUBLIC;

