SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_rep_he_elem_mat ( nr_seq_ele_rep_p bigint, nm_usuario_p text, ie_processo_hidrico_p text) AS $body$
DECLARE


nr_seq_elem_mat_he_w		bigint;
nr_seq_he_elem_mat_w		bigint;
qt_conversao_ml_w			double precision;
qt_maior_w					double precision := 0;

c01 CURSOR FOR
SELECT	b.nr_sequencia,
		a.qt_conversao_ml
from 	nut_elem_material a,
		prescr_rep_he_elem_mat b
where	a.nr_sequencia = b.nr_seq_elem_mat
and		b.nr_seq_ele_rep = nr_seq_ele_rep_p
and		a.ie_situacao	= 'A'
and		a.ie_padrao	= 'S'
and		coalesce(a.ie_tipo,'NPT') = 'NPT';


BEGIN

if (ie_processo_hidrico_p = 'S') then
	open c01;
	loop
	fetch c01 into	nr_seq_elem_mat_he_w,
					qt_conversao_ml_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		if (qt_maior_w < qt_conversao_ml_w) then
			qt_maior_w 	:= qt_conversao_ml_w;
			nr_seq_he_elem_mat_w := nr_seq_elem_mat_he_w;
		end if;
		end;
	end loop;
	close c01;

	delete from prescr_rep_he_elem_mat
	where  nr_sequencia = nr_seq_he_elem_mat_w;
	commit;

end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
