SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_resumo_lote_js ( nr_seq_lote_p bigint, nr_seq_caixa_p bigint, dt_fechamento_lote_p INOUT text, vl_saldo_monetario_p INOUT bigint, vl_saidas_lote_p INOUT bigint, vl_entradas_lote_p INOUT bigint, nm_usuario_p text) AS $body$
BEGIN

	-- Data Fechamento Lote
	select	max(to_char(dt_fechamento_lote, 'dd/mm/yyyy hh24:mi:ss'))
	into STRICT	dt_fechamento_lote_p
	from	movto_trans_financ
	where	nr_seq_lote = nr_seq_lote_p
	and	nr_seq_caixa = nr_seq_caixa_p;

	--Saldo Monetario
	select	max(obter_saldo_movto_trans_financ(nr_seq_lote, nr_seq_caixa_p,'M'))
	into STRICT	vl_saldo_monetario_p
	from	movto_trans_financ
	where	nr_seq_lote = nr_seq_lote_p;

	--Sa√≠das Lote
	select	max(obter_saldo_movto_trans_financ(nr_seq_lote,nr_seq_caixa_p,'S'))
	into STRICT	vl_saidas_lote_p
	from	movto_trans_financ
	where	nr_seq_lote = nr_seq_lote_p;

	--Entradas Lote
	select	max(obter_saldo_movto_trans_financ(nr_seq_lote,nr_seq_caixa_p,'E'))
	into STRICT	vl_entradas_lote_p
	from	movto_trans_financ
	where	nr_seq_lote = nr_seq_lote_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
