CREATE OR REPLACE PROCEDURE atualizar_resumo_lote ( nr_seq_lote_p bigint, nr_sequencia_p bigint, dt_fechamento_lote_p INOUT timestamp, vl_saldo_lote_p INOUT bigint, vl_debito_lote_p INOUT bigint, vl_credito_lote_p INOUT bigint) AS $body$
BEGIN
if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '')
	and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
	select 	dt_fechamento_lote
	into STRICT	dt_fechamento_lote_p
	from 	movto_trans_financ
	where 	nr_seq_lote = nr_seq_lote_p
	and  	nr_seq_caixa = nr_sequencia_p;

	select 	obter_saldo_movto_trans_financ(nr_seq_lote, nr_sequencia_p, 'M'),
		obter_saldo_movto_trans_financ(nr_seq_lote, nr_sequencia_p, 'S'),
		obter_saldo_movto_trans_financ(nr_seq_lote, nr_sequencia_p, 'E')
	into STRICT	vl_saldo_lote_p,
		vl_debito_lote_p,
		vl_credito_lote_p
	from 	movto_trans_financ
	where 	nr_seq_lote = nr_seq_lote_p;
	end;
end if;
end;
$body$
