CREATE OR REPLACE PROCEDURE atualizar_resumo_migracao (nm_usuario_p text) AS $body$
DECLARE


hr_horario_w	smallint;
ie_dia_w	smallint;


BEGIN
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	hr_horario_w 	:= (to_char(trunc(clock_timestamp(),'hh24'),'hh24'))::numeric;
	ie_dia_w	:= (obter_cod_dia_semana(clock_timestamp()))::numeric;

	if (ie_dia_w not in (1,7)) and (hr_horario_w in (9,12,15,18)) then
		begin
		CALL gerar_resumo_migracao(trunc(clock_timestamp(),'hh24'), nm_usuario_p);
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
