CREATE OR REPLACE PROCEDURE atualizar_saida_atend_visita ( nr_atendimento_p bigint, qt_dia_p bigint, nr_senha_espera_p INOUT bigint, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
BEGIN
 
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then 
	gerar_visitante_saida(nr_sequencia_p, nm_usuario_p, ' ', 'C');
end if;
	 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '' AND qt_dia_p IS NOT NULL AND qt_dia_p::text <> '') then 
	select	min(nr_senha_espera) 
	into STRICT	nr_senha_espera_p 
	from	atendimento_visita 
	where	nr_atendimento	= nr_atendimento_p 
	and	dt_entrada between clock_timestamp() - qt_dia_p and clock_timestamp() 
	and	ie_status	= 'A' 
	and	coalesce(dt_saida::text, '') = '';
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
