SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_saldo_cartao_cr ( nr_seq_movto_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_saldo_w		double precision;
vl_baixas_w		double precision;
vl_liquido_w		double precision;
nr_seq_parcela_w		bigint;
ds_comprovante_w		varchar(255);
nr_autorizacao_w		varchar(255);

c01 CURSOR FOR
SELECT	nr_sequencia
from	movto_cartao_cr_parcela
where	nr_seq_movto	= nr_seq_movto_p;


BEGIN

select	obter_vl_liquido_cartao(nr_seq_movto_p)
into STRICT	vl_liquido_w
;

select	coalesce(sum(vl_baixa),0)
into STRICT	vl_baixas_w
from	movto_cartao_cr_baixa
where	nr_seq_movto	= nr_seq_movto_p;

select	max(ds_comprovante),
	max(nr_autorizacao)
into STRICT	ds_comprovante_w,
	nr_autorizacao_w
from	movto_cartao_cr
where	nr_sequencia	= nr_seq_movto_p;

if (vl_baixas_w > vl_liquido_w) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(282090,'VL_BAIXAS='||vl_baixas_w ||';NR_SEQ_MOVTO='|| nr_seq_movto_p ||';DS_COMPROVANTE='|| ds_comprovante_w ||';NR_AUTORIZACAO='||nr_autorizacao_w );
end if;

select	obter_saldo_cartao_cr(nr_seq_movto_p,null)
into STRICT	vl_saldo_w
;

update	movto_cartao_cr
set	dt_baixa	= CASE WHEN vl_saldo_w=0 THEN clock_timestamp()  ELSE null END ,
	ie_situacao	= CASE WHEN vl_saldo_w=0 THEN 'L'  ELSE ie_situacao END
where	nr_sequencia	= nr_seq_movto_p;

open c01;
loop
fetch c01 into
	nr_seq_parcela_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	CALL atualizar_saldo_cartao_parcela(nr_seq_parcela_w,nm_usuario_p);
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualizar_saldo_cartao_cr ( nr_seq_movto_p bigint, nm_usuario_p text) FROM PUBLIC;

