SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_saldo_extrato_js ( nr_seq_extrato_p bigint, cd_banco_p bigint) AS $body$
DECLARE


vl_saldo_final_w 	double precision;
vl_saldo_inicial_w	double precision;


BEGIN
if (nr_seq_extrato_p IS NOT NULL AND nr_seq_extrato_p::text <> '') then
	begin

	select	max(coalesce(vl_saldo_inicial,0))
	into STRICT	vl_saldo_inicial_w
	from	banco_extrato
	where	nr_sequencia	= nr_seq_extrato_p;

	select  sum(CASE WHEN a.ie_deb_cred='D' THEN  a.vl_lancamento * -1  ELSE a.vl_lancamento END )
	into STRICT	vl_saldo_final_w
	from    banco_extrato_lanc a
	where   substr(obter_se_banco_hist_saldo(cd_banco_p, a.cd_historico),1,255)  = 'N'
	and     a.nr_seq_extrato  = nr_seq_extrato_p;

	update  banco_extrato
	set     vl_saldo_final = coalesce(vl_saldo_inicial_w,0) + coalesce(vl_saldo_final_w,0)
	where   nr_sequencia   = nr_seq_extrato_p;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
