SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_saldo_movto_bco_pend ( nr_seq_movto_pend_p bigint, nm_usuario_p text, ie_commit_p text) AS $body$
DECLARE


vl_baixa_w		double precision;
vl_credito_w		double precision;
vl_juros_w		double precision;
vl_multa_w		double precision;
vl_saldo_atual_w	double precision;
dt_baixa_w		timestamp;
/* Projeto Multimoeda - Variáveis*/

vl_credito_estrang_w	double precision;
vl_baixa_estrang_w	double precision;
vl_cotacao_w		cotacao_moeda.vl_cotacao%type;


BEGIN
if (nr_seq_movto_pend_p IS NOT NULL AND nr_seq_movto_pend_p::text <> '') then
	select	vl_credito,
		vl_credito_estrang  -- Projeto Multimoeda - Busca o valor do crédito em moeda estrangeira
	into STRICT	vl_credito_w,
		vl_credito_estrang_w
	from	movto_banco_pend a
	where	a.nr_sequencia	= nr_seq_movto_pend_p;

	select	coalesce(sum(a.vl_juros),0),
		coalesce(sum(a.vl_multa),0),
		coalesce(sum(a.vl_baixa),0),
		coalesce(sum(a.vl_baixa_estrang),0)  -- Projeto Multimoeda - Busca o valor da baixa em moeda estrangeira
	into STRICT	vl_juros_w,
		vl_multa_w,
		vl_baixa_w,
		vl_baixa_estrang_w
	FROM movto_banco_pend_baixa a
LEFT OUTER JOIN tipo_baixa_cni b ON (a.nr_seq_tipo_baixa = b.nr_sequencia)
WHERE a.nr_seq_movto_pend	= nr_seq_movto_pend_p;

	select	max(dt_baixa)
	into STRICT	dt_baixa_w
	from	movto_banco_pend_baixa
	where	nr_seq_movto_pend	= nr_seq_movto_pend_p;

	vl_saldo_atual_w	:= vl_credito_w - vl_baixa_w - vl_juros_w - vl_multa_w;

	/* Projeto Multimoeda - Verifica se o movto pendente é em moeda estrangeira, se sim busca a cotação da baixa mais recente para converter o saldo em moeda estrangeira*/

	if (coalesce(vl_credito_estrang_w,0) <> 0) then
		select max(vl_cotacao)
		into STRICT vl_cotacao_w
		from movto_banco_pend_baixa
		where nr_seq_movto_pend = nr_seq_movto_pend_p
		  and dt_baixa = dt_baixa_w;
		if (coalesce(vl_cotacao_w,0) <> 0) then
			vl_saldo_atual_w := (vl_credito_estrang_w - vl_baixa_estrang_w) * vl_cotacao_w;
		end if;
	end if;

	if (vl_saldo_atual_w < 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(115731,'VL_BAIXA=' || (vl_baixa_w + vl_juros_w + vl_multa_w) || ';' || 'VL_CREDITO=' || vl_credito_w);
	end if;


	update	movto_banco_pend
	set	vl_saldo	= abs(vl_saldo_atual_w),
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp(),
		dt_liquidacao	= CASE WHEN vl_saldo_atual_w=0 THEN coalesce(dt_baixa_w,clock_timestamp())  ELSE null END
	where	nr_sequencia	= nr_seq_movto_pend_p;

end if;

if (ie_commit_p = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
