CREATE OR REPLACE PROCEDURE atualizar_san_coleta_amostra ( nr_seq_doacao_p bigint, nm_usuario_p text) AS $body$
DECLARE



ie_realiza_nat_w	varchar(1);
qtd_w			integer	:= 1;

BEGIN

select 	coalesce(ie_realiza_nat,'N')
into STRICT	ie_realiza_nat_w
from	san_doacao
where	nr_sequencia = nr_seq_doacao_p;

if ( ie_realiza_nat_w = 'S') then
	qtd_w:= 3;
end if;

for i in 1..qtd_w loop
	insert into san_coleta_amostra(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_doacao )
	values (
		nextval('san_coleta_amostra_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_doacao_p
	);
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
