CREATE OR REPLACE PROCEDURE atualizar_satisf_os_encerrada ( qt_dia_p bigint, ie_grau_satisfacao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w			man_ordem_servico.nr_sequencia%type;

c01 CURSOR FOR
SELECT	nr_sequencia
from	man_ordem_servico
where	ie_status_ordem = 3
and	(dt_fim_real IS NOT NULL AND dt_fim_real::text <> '')
and	coalesce(ie_grau_satisfacao::text, '') = ''
and	trunc(dt_fim_real) + qt_dia_p <= trunc(clock_timestamp());


BEGIN
CALL wheb_usuario_pck.SET_IE_EXECUTAR_TRIGGER('N');

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	man_ordem_servico
	set	ie_grau_satisfacao		= ie_grau_satisfacao_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_sequencia_w;

	insert into man_ordem_serv_tecnico(
				nr_sequencia,
				nr_seq_ordem_serv,
				dt_atualizacao,
				nm_usuario,
				ds_relat_tecnico,
				dt_historico,
				ie_origem,
				dt_liberacao,
				nm_usuario_lib)
			values (	nextval('man_ordem_serv_tecnico_seq'),
				nr_sequencia_w,
				clock_timestamp(),
				nm_usuario_p,
				substr(wheb_mensagem_pck.get_texto(305688,'QT_DIA=' || qt_dia_p),1,4000),
				clock_timestamp(),
				'I',
				clock_timestamp(),
				nm_usuario_p);
	end;
end loop;
close C01;

commit;

CALL wheb_usuario_pck.SET_IE_EXECUTAR_TRIGGER('S');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
