CREATE OR REPLACE PROCEDURE atualizar_selec_pagar_escrit ( nr_seq_escrit_p bigint, nr_titulo_p bigint) AS $body$
BEGIN

if (nr_seq_escrit_p > 0) then
	begin
	update	titulo_pagar_escrit
	set	ie_selecionado = CASE WHEN coalesce(ie_selecionado,'N')='N' THEN 'S'  ELSE 'N' END
	where	nr_seq_escrit	= nr_seq_escrit_p
	and	nr_titulo		= nr_titulo_p;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
