SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_seq_apres_acao_teste ( nr_seq_item_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w			bigint;
nr_seq_apres_w			integer := 0;
qt_existe_w			bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	teste_caso_teste_it_acao
	where	nr_seq_item = nr_seq_item_p
	order by nr_seq_apres, nr_sequencia;


BEGIN

if (coalesce(nr_seq_item_p,0) > 0) then
	begin
	open C01;
	loop
	fetch C01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		nr_seq_apres_w := nr_seq_apres_w + 1;
		update	teste_caso_teste_it_acao
		set	nr_seq_apres = nr_seq_apres_w,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	nr_sequencia = nr_sequencia_w;

		select	count(*)
		into STRICT	qt_existe_w
		from	teste_caso_teste_it_acao
		where	nr_seq_acao_ant = nr_sequencia_w;

		if (qt_existe_w > 0) then
			update	teste_caso_teste_it_acao
			set	nr_seq_apres = nr_seq_apres_w,
				dt_atualizacao = clock_timestamp(),
				nm_usuario = nm_usuario_p
			where	nr_seq_acao_ant = nr_sequencia_w;
		end if;
		end;
	end loop;
	close C01;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
