CREATE OR REPLACE PROCEDURE atualizar_seq_cliente_contrato ( nr_seq_cliente_p bigint, nr_sequencia_contrato_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_cgc_contratado_w varchar(50);
cd_cnpj_w varchar(50);


BEGIN
select	a.cd_cgc_contratado
into STRICT	cd_cgc_contratado_w
from	contrato a
where	a.nr_sequencia = nr_sequencia_contrato_p;

select	a.cd_cnpj
into STRICT	cd_cnpj_w
from	com_cliente a
where	nr_sequencia = nr_seq_cliente_p;

if (cd_cnpj_w <> cd_cgc_contratado_w) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(247977);
end if;

update	contrato
set	nr_seq_cliente	= nr_seq_cliente_p,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_sequencia_contrato_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
