SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_setor_auditoria ( nr_sequencia_p bigint, ie_tipo_item_p bigint, nr_seq_atepacu_p bigint, nr_seq_motivo_p bigint) AS $body$
DECLARE


ie_altera_item_repasse_w	varchar(1) := 'S';
nm_usuario_w			usuario.nm_usuario%type := wheb_usuario_pck.Get_Nm_usuario;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type := wheb_usuario_pck.get_cd_estabelecimento;

BEGIN


ie_altera_item_repasse_w := obter_param_usuario(1116, 189, obter_perfil_ativo, nm_usuario_w, cd_estabelecimento_w, ie_altera_item_repasse_w);


if (ie_tipo_item_p = 1) then
	update	auditoria_propaci a
	set	a.nr_seq_atepacu_ajuste = nr_seq_atepacu_p,
		a.ie_tipo_auditoria = 'S',
		a.nr_seq_motivo = CASE WHEN coalesce(nr_seq_motivo_p,0)=0 THEN  a.nr_seq_motivo  ELSE nr_seq_motivo_p END ,
		a.nm_usuario	= nm_usuario_w
	where	nr_sequencia = nr_sequencia_p
	and	not exists (	SELECT	1
				from	procedimento_repasse x
				where	x.nr_seq_procedimento = a.nr_seq_propaci
				and	coalesce(ie_altera_item_repasse_w,'S') = 'N');

else
	update	auditoria_matpaci a
	set	a.nr_seq_atepacu_ajuste = nr_seq_atepacu_p,
		a.ie_tipo_auditoria 	= 'S',
		a.nr_seq_motivo 	= CASE WHEN coalesce(nr_seq_motivo_p,0)=0 THEN  a.nr_seq_motivo  ELSE nr_seq_motivo_p END ,
		a.nm_usuario		= nm_usuario_w
	where	a.nr_sequencia 		= nr_sequencia_p
	and	not exists (	SELECT	1
				from	material_repasse x
				where	x.nr_seq_material = a.nr_seq_matpaci
				and	coalesce(ie_altera_item_repasse_w,'S') = 'N');

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
