SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_setor_pac_procmat (nr_seq_procmat_p bigint, ie_proc_mat_p text) AS $body$
DECLARE



cd_setor_atend_w	bigint;



BEGIN

if (ie_proc_mat_p	= 'M') then
	begin

	select	coalesce(max(Obter_Setor_Atend_Data(nr_atendimento, dt_atendimento)),0)
	into STRICT	cd_setor_atend_w
	from	material_atend_paciente
	where	nr_sequencia	= nr_seq_procmat_p;


	if (cd_setor_atend_w > 0) then

		update	material_atend_paciente
		set	cd_setor_paciente	= cd_setor_atend_w
		where	nr_sequencia		= nr_seq_procmat_p
		and	coalesce(cd_setor_paciente::text, '') = '';


	end if;


	end;
elsif (ie_proc_mat_p	= 'P') then
	begin

	select	coalesce(max(Obter_Setor_Atend_Data(nr_atendimento, dt_procedimento)),0)
	into STRICT	cd_setor_atend_w
	from	procedimento_paciente
	where	nr_sequencia	= nr_seq_procmat_p;


	if (cd_setor_atend_w > 0) then

		update	procedimento_paciente
		set	cd_setor_paciente	= cd_setor_atend_w
		where	nr_sequencia		= nr_seq_procmat_p
		and	coalesce(cd_setor_paciente::text, '') = '';


	end if;


	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
