CREATE OR REPLACE PROCEDURE atualizar_setor_triagem ( nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_setor_atendimento_w			bigint;
nr_seq_triagem_w				bigint;


BEGIN 
select	coalesce(max(cd_setor_atendimento),0), 
		max(nr_sequencia) 
into STRICT	cd_setor_atendimento_w, 
		nr_seq_triagem_w 
from	triagem_pronto_atend 
where	nr_atendimento = nr_atendimento_p;
 
if (cd_setor_atendimento_w = 0) then 
	select	coalesce(max(substr(obter_setor_atendimento(nr_atendimento_p),1,5)),0) 
	into STRICT	cd_setor_atendimento_w 
	;
	 
	if (cd_setor_atendimento_w > 0) then 
		update	triagem_pronto_atend 
		set		cd_setor_atendimento = cd_setor_atendimento_w 
		where	nr_sequencia = nr_seq_triagem_w;
	end if;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
