CREATE OR REPLACE PROCEDURE atualizar_simp_preco ( nm_usuario_p text, cd_tuss_p bigint, cd_simpro_p bigint, dt_vigencia_p timestamp) AS $body$
DECLARE


cd_estab_simpro_w	estabelecimento.cd_estabelecimento%type;


BEGIN

cd_estab_simpro_w := wheb_usuario_pck.get_cd_estabelecimento;

update	simpro_preco
set	cd_tuss = cd_tuss_p,
	dt_atualizacao = clock_timestamp(),
	nm_usuario = nm_usuario_p
where 	cd_simpro = cd_simpro_p
