SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_situacao_emprestimo ( nr_emprestimo_p bigint) AS $body$
DECLARE


qt_itens_w		bigint;
qt_material_w		double precision;
ie_situacao_w		varchar(1);


BEGIN

select	coalesce(count(*),0)
into STRICT	qt_itens_w
from	emprestimo_material
where	nr_emprestimo = nr_emprestimo_p;

select	ie_situacao
into STRICT	ie_situacao_w
from	emprestimo
where	nr_emprestimo = nr_emprestimo_p;

if (qt_itens_w > 0) then

	select	coalesce(sum(qt_material),0)
	into STRICT	qt_material_w
	from	emprestimo_material
	where	nr_emprestimo = nr_emprestimo_p;

	ie_situacao_w:= 'A';
	if (qt_material_w = 0) then
		ie_situacao_w:= 'B';
	end if;

	update	emprestimo
	set	ie_situacao = ie_situacao_w
	where 	nr_emprestimo = nr_emprestimo_p;

	commit;

elsif (ie_situacao_w = 'B') then

	update	emprestimo
	set	ie_situacao = 'A'
	where 	nr_emprestimo = nr_emprestimo_p;

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
