SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_situacao_leito ( cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text, ie_situacao_p text, nm_usuario_p text, ds_erro_p INOUT text, ie_verifica_clique_p text) AS $body$
DECLARE


nr_agrupamento_w	integer;
cd_tipo_acomodacao_w	smallint;
ie_status_unidade_w	varchar(3);
qt_unidade_ocupado_w	integer;
ie_nova_situacao_w	varchar(1);
ie_agrup_automatico_w	varchar(1);


BEGIN

ie_agrup_automatico_w := Obter_Param_Usuario(916, 737, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_agrup_automatico_w);

ie_nova_situacao_w	:= 'A';

if (ie_situacao_p = 'A') then
	ie_nova_situacao_w	:= 'I';
end if;

select	max(nr_agrupamento),
	max(cd_tipo_acomodacao),
	max(ie_status_unidade)
into STRICT	nr_agrupamento_w,
	cd_tipo_acomodacao_w,
	ie_status_unidade_w
from	unidade_atendimento
where	cd_setor_atendimento 	= cd_setor_atendimento_p
and	cd_unidade_basica	= cd_unidade_basica_p
and	cd_unidade_compl	= cd_unidade_compl_p;



if (nr_agrupamento_w IS NOT NULL AND nr_agrupamento_w::text <> '') then

	if (ie_status_unidade_w = 'A') or (ie_status_unidade_w = 'P') then

		ds_erro_p	:= WHEB_MENSAGEM_PCK.get_texto(277322,null);
	elsif (ie_status_unidade_w	= 'H') then
		ds_erro_p	:= WHEB_MENSAGEM_PCK.get_texto(277323,null);
	else
		select 	count(*)
		into STRICT	qt_unidade_ocupado_w
		from	unidade_atendimento
		where	nr_agrupamento 		= nr_agrupamento_w
		and	cd_setor_atendimento 	= cd_setor_atendimento_p
		and	(((coalesce(ie_agrup_automatico_w,'N') = 'S') and (cd_tipo_acomodacao <> cd_tipo_acomodacao_w)) or (coalesce(ie_agrup_automatico_w,'N') = 'N'))
		and (cd_unidade_basica <> cd_unidade_basica_p or cd_unidade_compl <> cd_unidade_compl_p)
		and	ie_status_unidade in ('A','P');

		if (qt_unidade_ocupado_w = 0) then

			update 	unidade_atendimento
			set	ie_situacao 		= ie_situacao_p,
				nm_usuario		= nm_usuario_p,
				dt_atualizacao		= clock_timestamp()
			where	cd_setor_atendimento 	= cd_setor_atendimento_p
			and	cd_tipo_acomodacao 	= cd_tipo_acomodacao_w
			and (cd_unidade_basica 	<> cd_unidade_basica_p
				or cd_unidade_compl 	<> cd_unidade_compl_p
				or ie_verifica_clique_p = 'S')
			and	nr_agrupamento 		= nr_agrupamento_w;

			update 	unidade_atendimento
			set	ie_situacao 		= ie_nova_situacao_w,
				nm_usuario		= nm_usuario_p,
				dt_atualizacao		= clock_timestamp()
			where	cd_setor_atendimento 	= cd_setor_atendimento_p
			and	cd_tipo_acomodacao 	<> cd_tipo_acomodacao_w
			and (cd_unidade_basica 	<> cd_unidade_basica_p
				or cd_unidade_compl 	<> cd_unidade_compl_p
				or ie_verifica_clique_p = 'S')
			and	nr_agrupamento 		= nr_agrupamento_w;

		else
			ds_erro_p	:= WHEB_MENSAGEM_PCK.get_texto(277324,null) || nr_agrupamento_w;

		end if;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
