SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_situacao_unidades (nr_seq_unidade_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_agrupamento_w	               integer;
cd_setor_atendimento_w	integer;
cd_tipo_acomodacao_w	smallint;
qt_unidade_ocupado_w    integer;


BEGIN

select	max(nr_agrupamento),
	max(cd_setor_atendimento),
	max(cd_tipo_acomodacao)
into STRICT	nr_agrupamento_w,
	cd_setor_atendimento_w,
	cd_tipo_acomodacao_w
from	unidade_atendimento
where	nr_seq_interno = nr_seq_unidade_p;

select 	count(*)
into STRICT	qt_unidade_ocupado_w
from	unidade_atendimento
where	nr_agrupamento 		= nr_agrupamento_w
and	cd_setor_atendimento 	= cd_setor_atendimento_w
and	ie_status_unidade in ('A','P');

if (qt_unidade_ocupado_w > 0) then

	update 	unidade_atendimento a
	set	a.ie_situacao 		= 'A',
		a.nm_usuario		= nm_usuario_p,
	        a.dt_atualizacao	= clock_timestamp()
        where	nr_seq_interno          = nr_seq_unidade_p;
else
	update 	unidade_atendimento a
	set	a.ie_situacao 		= 'A',
		a.nm_usuario		= nm_usuario_p,
	        a.dt_atualizacao	= clock_timestamp()
        where	a.cd_setor_atendimento 	= cd_setor_atendimento_w
	and	a.nr_agrupamento 	= nr_agrupamento_w;

end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
