CREATE OR REPLACE PROCEDURE atualizar_situacao_usuario ( ie_situacao_p text, cd_pessoa_fisica_p text) AS $body$
BEGIN

if (ie_situacao_p IS NOT NULL AND ie_situacao_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin
	update	usuario
	set	ie_situacao		= ie_situacao_p,
		dt_inativacao		= CASE WHEN ie_situacao_p='I' THEN clock_timestamp()  ELSE null END
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p;
	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
