CREATE OR REPLACE PROCEDURE atualizar_sit_mat_brasindice ( nr_sequencia_p bigint, ie_situacao_p text, nm_usuario_p text) AS $body$
DECLARE


ie_situacao_w varchar(1);


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_situacao_p IS NOT NULL AND ie_situacao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	if ('A' = ie_situacao_p) then
		ie_situacao_w := 'I';
        else
		ie_situacao_w := 'A';
        end if;

	update	material_brasindice
	set	ie_situacao	= ie_situacao_w,
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_sequencia_p;

	end;
end if;

commit;

end;
$body$
