SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_sit_titulo_pagar ( nr_titulo_p bigint, nm_usuario_p text) AS $body$
BEGIN
	if (coalesce(nr_titulo_p,0) <> 0) and (coalesce(nm_usuario_p,'') <> '') then
		update	titulo_pagar
		set      ie_situacao    = 'B',
			dt_atualizacao	= clock_timestamp(),
			nm_usuario     	= nm_usuario_p
		where  	nr_titulo      	= nr_titulo_p;
	end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
